'use strict';

let path = require('path');
let Sassaby = require('sassaby');
let sassUtilsDependencies = require('@wk/sass-utils/config').dependencyMap;

describe('mixin wk-tooltip', () => {

    let file = path.resolve('src/wk-components/tooltip/_tooltip.mixins.scss');
    let sb = new Sassaby(file, {
        variables: {
            'minor-second': 1,
            'wk-alert-link-color': '#000',
            'wk-state-info-link-color': '#CCC',
            'wk-state-success-text-color': '#CCC',
            'wk-state-danger-link-color': '#CCC',
            'wk-tooltip-background-color': '#fff',
            'wk-tooltip-border': '#CCC',
            'wk-tooltip-border-color': '#CCC',
            'wk-tooltip-box-shadow': '#CCC'
        },
        dependencies: sassUtilsDependencies.concat([
            'src/wk-components/tooltip/_tooltip.variables.scss',
            'src/wk-components/type/_type.mixins.scss'
        ])
    });

    describe('declares property', () =>  {
        let mixin = sb.includedMixin('wk-tooltip').called();

        it('background', () => mixin.declares('background', '#fff'));
    });

    describe('creates selector', () =>  {
        let mixin = sb.standaloneMixin('wk-tooltip').called();

        it('wk-position-left::before', () => mixin.createsSelector('.wk-position-left::before'));
        it('wk-position-left::after', () => mixin.createsSelector('.wk-position-left::after'));
        it('wk-position-right::before', () => mixin.createsSelector('.wk-position-right::before'));
        it('wk-position-right::after', () => mixin.createsSelector('.wk-position-right::after'));
        it('wk-position-top::before', () => mixin.createsSelector('.wk-position-top::before'));
        it('wk-position-top::after', () => mixin.createsSelector('.wk-position-top::after'));
        it('wk-position-bottom::before', () => mixin.createsSelector('.wk-position-bottom::before'));
        it('wk-position-bottom::after', () => mixin.createsSelector('.wk-position-bottom::after'));
    });
});
