'use strict';

let path = require('path');
let Sassaby = require('sassaby');

describe('_wk-reset.scss', () => {
    let file = path.resolve('src/_wk-reset.scss');
    let sb = new Sassaby(file);

    describe('imports', () =>  {
        it('normalize', () => sb.imports('wk-lib/normalize'));
        it('post-normalize', () => sb.imports('wk-lib/post-normalize'));
    });
});
