'use strict';

let path = require('path');
let Sassaby = require('sassaby');

describe('wk-sass-utils.scss', () => {
    let file = path.resolve('src/wk-sass-utils.scss');
    let sb = new Sassaby(file);

    describe('imports dependencies', () => [
            'mixins',
            'vendors',
            'utilities'
        ].forEach(n => it('should import ' + n, () => sb.imports(n)))
    );
});
