'use strict';

var path = require('path');
var version = require(path.resolve('package.json')).version;
var xVersion = (/-/.test(version)) ? version : version.replace(/\.\d+$/, '.x');
var releaseVersion = process.env.RELEASE_VERSION ? process.env.RELEASE_VERSION : xVersion; // we don't really want an .x version if this is a pre-release version // Bamboo Hub doesn't support things like 0.0.0-rc.x or 0.0.x-rc.0

module.exports = {
    version: version,
    xVersion: xVersion,
    releaseVersion: releaseVersion
};
